export function View_search() { return `
<main class="search-page">
<div class="inner">
<div class="search-head">
<h1>검색 결과</h1>
<div class="search-meta" id="searchMeta">검색어: <strong id="kw"></strong></div>
</div>
<!-- 필터(선택) -->
<div class="filters" style="margin-bottom:16px; display:flex; gap:10px; flex-wrap:wrap;">
<button class="button ghost" data-filter="all">전체</button>
<button class="button ghost" data-filter="lol">LoL</button>
<button class="button ghost" data-filter="nba">NBA</button>
<button class="button ghost" data-filter="epl">EPL</button>
<button class="button ghost" data-filter="news">뉴스</button>
<button class="button ghost" data-filter="match">경기</button>
<button class="button ghost" data-filter="store">스토어</button>
</div>
<div class="search-meta" id="resultCount" style="margin-bottom:10px;"></div>
<section aria-live="polite" class="result-grid" id="results"></section>
<div class="pager" hidden="" id="pager">
<button class="button tertiary" id="prevPage">이전</button>
<button class="button tertiary" id="nextPage">다음</button>
</div>
<div class="search-empty" hidden="" id="empty">
<p>검색 결과가 없습니다. 다른 키워드를 입력해 보세요.<br/>예) <em>Faker</em>, <em>손흥민</em>, <em>Arsenal</em>, <em>유니폼</em></p>
</div>
</div>
</main>
`; }
export function View_cart() { return `
<main class="cart-page">
<div class="cart-page inner cart-grid">
<div>
<div class="empty" hidden="" id="emptyState">장바구니가 비어 있습니다.</div>
<div id="cartList"></div>
</div>
<aside class="cart-summary">
<h3>주문 요약</h3>
<div class="summary-row"><span>상품 금액</span><strong id="subPrice">₩0</strong></div>
<div class="summary-row"><span>총 수량</span><strong id="itemCount">0</strong></div>
<div class="summary-row summary-total"><span>결제 금액</span><strong id="totalPrice">₩0</strong></div>
<button class="button primary" disabled="" id="checkoutBtn" type="button">결제하기</button>
</aside>
</div>
</main>
`; }
export function View_login() { return `
<main class="auth-page">
<div class="auth-box">
<h1>로그인</h1>
<form class="auth-form" id="loginForm">
<label for="email">이메일</label>
<input id="email" required="" type="email"/>
<label for="password">비밀번호</label>
<input id="password" required="" type="password"/>
<button class="button primary" type="submit">로그인</button>
</form>
<p class="link">계정이 없으신가요? <a href="signup.html">회원가입</a></p>
</div>
</main>
`; }
export function View_signup() { return `
<main class="auth-page">
<div class="auth-box">
<h1>회원가입</h1>
<form class="auth-form" id="signupForm">
<label for="nickname">닉네임</label>
<input id="nickname" required="" type="text"/>
<label for="email">이메일</label>
<input id="email" required="" type="email"/>
<label for="password">비밀번호</label>
<input id="password" minlength="6" required="" type="password"/>
<button class="button primary" type="submit">회원가입</button>
</form>
<p class="link">이미 계정이 있으신가요? <a href="login.html">로그인</a></p>
</div>
</main>
`; }
